import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBookOpen, faSearch } from '@fortawesome/free-solid-svg-icons';


const SearchPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = async () => {
        // Perform search logic here
        // You can use libraries like axios for making HTTP requests or any other search library

        // Mocking the search results for demonstration
        const mockResults = [
            { title: 'Search Result 1', url: 'https://example.com/search/result1' },
            { title: 'Search Result 2', url: 'https://example.com/search/result2' },
            { title: 'Search Result 3', url: 'https://example.com/search/result3' },
        ];

        setSearchResults(mockResults);

        let response = fetch('http://localhost:8000/search?query=this is fucking awesome')
        response = await response.json

    };

    return (
        <div>
            <div className='headerBackground'>
                <img src='https://pureportal.coventry.ac.uk/skin/headerImage/' />
            </div>

            <div className='searchSection'>
                <div>
                    <div className='header'>
                        <div>
                            <FontAwesomeIcon icon={faBookOpen} aria-hidden='true' style={{ marginRight: "20px", color: 'gray' }} />
                            <span>
                                Find Research Outputs
                            </span>
                        </div>
                    </div>
                    <div className='searchBox'>
                        <input
                            type="text"
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            placeholder="Enter your search query"
                        />
                        <button onClick={handleSearch}>
                        <FontAwesomeIcon icon={faSearch} aria-hidden='true' style={{ color: 'gray' }} />
                        </button>
                    </div>
                </div>
            </div>

            <h2>Search Results</h2>
            {searchResults.length > 0 ? (
                <ul>
                    {searchResults.map((result, index) => (
                        <li key={index}>
                            <a href={result.url}>{result.title}</a>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No search results found.</p>
            )}
        </div>
    );
};

export default SearchPage;
