import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBookOpen } from '@fortawesome/free-solid-svg-icons';


const SearchPage = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearch = () => {
        // Perform search logic here
        // You can use libraries like axios for making HTTP requests or any other search library

        // Mocking the search results for demonstration
        const mockResults = [
            { title: 'Search Result 1', url: 'https://example.com/search/result1' },
            { title: 'Search Result 2', url: 'https://example.com/search/result2' },
            { title: 'Search Result 3', url: 'https://example.com/search/result3' },
        ];

        setSearchResults(mockResults);
    };

    return (
        <div>
            <div className='headerBackground'>
                <img src='https://pureportal.coventry.ac.uk/skin/headerImage/' />
            </div>

            <div>
                <div className='header'>
                    <span >
                        <FontAwesomeIcon icon={faBookOpen} aria-hidden='true' />
                        Find Research Outputs
                    </span>
                </div>
            </div>
            <h1>Search Page</h1>
            <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Enter your search query"
            />
            <button onClick={handleSearch}>Search</button>

            <h2>Search Results</h2>
            {searchResults.length > 0 ? (
                <ul>
                    {searchResults.map((result, index) => (
                        <li key={index}>
                            <a href={result.url}>{result.title}</a>
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No search results found.</p>
            )}
        </div>
    );
};

export default SearchPage;
